def htmlremscript(html):
    """removes script tags from header of document
    - not tested
    """
    s=BeautifulSoup(html)
    script_tags = s('script')
    for t in script_tags:
        t.decompose()
    assert len(s('script')) == 0
    return s.prettify()

def cleandefhtml(html, word):
    s=BeautifulSoup(html)
    r=s.find('section', id='top-definitions-section').parent
    for t in r('a'):
        t.replaceWithChildren()
    for b in r('button'):
        b.decompose()
    with open(f'{word}.shortdef.html', 'w', encoding='utf-8') as f:
        f.write("<html>"+r.prettify()+"</html>")
    rx=re.compile(r"([\n ])+")
    print(rx.sub(r"\1",r.text))
    return "<html>"+r.prettify()+"</html>"

def readdefhtml():
    word = 'pluperfect'
