### getting the list of all irregular verbs
def getirregbyletter(let = "a"):
    
    res = requests.get(irregurl.format(let)) 
    if res.status_code != 200:
        return None
        #tbls = pd.read_html(res.content) # no tables on the page???
    soup=BeautifulSoup(res.content)#held in div tags with class = 'block'
    blocks = soup.findAll("div", class_="block")
    print(f"{len(blocks)} blocks found")
    wordlist = list()
    for b in blocks:
        wordlist.extend(b.text.splitlines())
    return [x for x in wordlist if '\xa0' not in x]

