def get_tables_from_html(html):
    soup = BeautifulSoup(html)
    #if we only miss the tables we must assume the moods, which might be wrong
    #divs = soup("div",  attrs={'id':"conjugation-content-wrapper"})
    #d1 = divs[0]
    ###FIX: participles are missing
    moods = soup.find_all('div', class_='_2v8iz7Ez')#_2v8iz7Ez is the div #_1btShz4h is the anchor inside the div
    moodslist = [x.text.strip() for x in moods]
    tbls = soup("table")
    assert len(moodlist), len(tbls[1:])

    #first table is participles with no header
    
    participles = pd.read_html(str(tbls[0]), index_col=0)[0].transpose()
    #clean colons from col labels
    participles.columns=[x.replace(':','') for x in participles.columns]
    
    ctbls = tbls[1:]
    conjdict = dict()
    #highlighted letters cause spaces in the output words #<span class="conj-irregular">e</span>
    #how do we tell these spaces from the spaces between words in complex conjugations?
    #seem to crop up only in simple tenses: Indicative, Subjunctive, Imperative
    #need to apply(str.replace, args=[" ",""]) to all cells
    
    for i in range(len(moodlist)):
        t=str(ctbls[i])
        conjdict[moodlist[i]]= pd.read_html(t, index_col=0, header=0)[0]
        
    #clean up spaces
    for x in ['Indicative', 'Subjunctive', 'Imperative']:
        conjdict[x] = conjdict[x].applymap(lambda x: x.replace(" ",""))
    # conjdict['Indicative'] = conjdict['Indicative'].applymap(lambda x: x.replace(" ",""))
    # conjdict['Subjunctive'] = conjdict['Subjunctive'].applymap(lambda x: x.replace(" ",""))
    # conjdict['Imperative'] = conjdict['Imperative'].applymap(lambda x: x.replace(" ",""))

    conjdict['participles'] = participles
    
    return conjdict

def get_conj_to_file(verb):
    r = requests.get(conjurl+verb)
    print(r.status_code, r.cookies)
    htmlout = r.content
    htmlout = htmlremscript(htmlout)#remove script tags and runs prettify
    with open(r'last.html', 'w', encoding='utf8') as f:
        f.write(htmlout)

def get_conj_from_web(verb):
    """do we want to use the dict directly
    or load it from the shelf as an interim test
    """
    r = requests.get(conjurl+verb)
    print(r.status_code, r.cookies)
    htmlout = r.content
    #res = pd.read_html(htmlout)
    if r.status_code == 200:
        assert len(htmlout) > 0
        conjdict=get_tables_from_html(htmlout)
        print(conjdict.keys())
        with shelve.open('spshelf') as s:
            s[verb] = conjdict
    else:
        print(f"bad status {r.status}")
        conjdict = None
        

    
    return conjdict

